var Elixir = require('laravel-elixir');

require('require-dir')('./tasks');

/**
 * Build all the assets needed for Materialize.
 */
Elixir.extend('materialize', function() {
    Elixir(function(mix) {
        mix
        .materializeFonts()
        .materializeSass()
        .materializeJs();
        //no images in materialize-css
        //.materializeImages();
    });
});
